function randomBetween(n,e){return n<0?n+Math.random()*(Math.abs(n)+e):n+Math.random()*e}$.fn.equalizerAnimation=function(n){var e=$(this);setInterval(function(){e.find("span").eq(0).css({height:randomBetween(20,80)+"px"}),e.find("span").eq(1).css({height:randomBetween(30,100)+"px"}),e.find("span").eq(2).css({height:randomBetween(40,100)+"px"}),e.find("span").eq(3).css({height:randomBetween(30,90)+"px"}),e.find("span").eq(4).css({height:randomBetween(30,70)+"px"})},n),e.on("click",function(){e.toggleClass("paused")})},$(".equalizer").equalizerAnimation(180),$(".mobile").on("click",function(){console.log("ok"),$("nav ul").toggleClass("showing"),$(".bar").toggleClass("animate")});